{
  "query": "mutation Register($input: RegisterInput!) {
  register(input: $input) {
    token
    user {
      id
      username
      email
      wins
      losses
      gamesPlayed
      avatarUrl
    }
  }
}",
  "variables": {
    "input": {
      "username": "demo-user",
      "email": "demo@example.com",
      "password": "Password123"
    }
  }
}

{
  "query": "mutation Login($input: LoginInput!) {
  login(input: $input) {
    token
    user {
      id
      username
      email
      wins
      losses
      gamesPlayed
      avatarUrl
    }
  }
}",
  "variables": {
    "input": {
      "email": "demo@example.com",
      "password": "Password123"
    }
  }
}

{
  "query": "query Me {
  me {
    id
    username
    email
    wins
    losses
    gamesPlayed
    avatarUrl
  }
}"
}

{
  "query": "query MyShips($roomId: ID!) {
  myShips(roomId: $roomId) {
    id
    playerId
    roomId
    startX
    startY
    length
    orientation
    hits
  }
}",
  "variables": {
    "roomId": "{{roomId}}"
  }
}

{
  "query": "mutation PlaceShips($input: PlaceShipsInput!) {\n  placeShips(input: $input) {\n    id\n    playerId\n    roomId\n    startX\n    startY\n    length\n    orientation\n    hits\n  }\n}",
  "variables": {
    "input": {
      "roomId": "{{roomId}}",
      "ships": [
        { "startX": 0, "startY": 0, "length": 4, "orientation": "horizontal" },
        { "startX": 0, "startY": 2, "length": 3, "orientation": "horizontal" },
        { "startX": 5, "startY": 2, "length": 3, "orientation": "vertical" },
        { "startX": 2, "startY": 5, "length": 2, "orientation": "horizontal" },
        { "startX": 7, "startY": 5, "length": 2, "orientation": "vertical" },
        { "startX": 4, "startY": 8, "length": 2, "orientation": "horizontal" },
        { "startX": 0, "startY": 8, "length": 1, "orientation": "horizontal" },
        { "startX": 2, "startY": 8, "length": 1, "orientation": "horizontal" },
        { "startX": 6, "startY": 8, "length": 1, "orientation": "horizontal" },
        { "startX": 8, "startY": 8, "length": 1, "orientation": "horizontal" }
      ]
    }
  }
}

{
  "query": "query Messages($roomId: ID!) {
  messages(roomId: $roomId) {
    id
    roomId
    userId
    username
    text
    timestamp
    createdAt
    updatedAt
  }
}",
  "variables": {
    "roomId": "{{roomId}}"
  }
}

{
  "query": "mutation SendMessage($input: SendMessageInput!) {
  sendMessage(input: $input) {
    id
    roomId
    userId
    username
    text
    timestamp
    createdAt
    updatedAt
  }
}",
  "variables": {
    "input": {
      "roomId": "{{roomId}}",
      "text": "Hello from Postman!"
    }
  }
}
